import React from 'react';
import Stage from './Stage';
import Display from './Display';
import StartButton from './StartButton';
import { createStage } from '../gameHelpers';

const Tetris = () => {

    return (
        <div>
            <Stage stage={createStage()} />
            {/* sidebar */}
            <aside>
                <div>
                <Display text="Score" />
                <Display text="Rows" />
                <Display text="Level" />
                </div>

                <StartButton />



            </aside>
        </div>
    );
};

export default Tetris;
